      <div class="font-weight-bold text-white h2 mb-4 hero-title">
  Find the hottest events today!
</div>

<!-- Search Input -->
<div class="position-relative search-box">
  <div class="d-flex bg-white p-3 rounded-5 shadow">
    <div class="d-flex align-items-center w-100">
      <i class="bi bi-search text-muted me-2"></i>
      <input
        type="text"
        placeholder="Search for Events..."
        class="form-control border-0"
        v-model="searchQuery"
        @input="handleSearch"
      />
          </div>
        </div>

        <!-- Search Results -->
        <div
          v-if="searchQuery.trim()"
          class="position-absolute w-100 mt-2 bg-white rounded shadow-lg overflow-hidden"
        >
          <div v-if="isSearching" class="p-4 text-muted text-center">
            Searching...
          </div>

          <template v-else>
            <div v-if="searchResults.length" class="search-results">
              <div
                v-for="event in searchResults"
                :key="event.id"
                class="p-3 border-bottom hover-bg-light cursor-pointer"
                @click="selectEvent(event)"
              >
                <RouterLink
                  :to="{ name: 'EventDetail', params: { id: event.id } }"
                  class="custom-link"
                >
                  <div class="d-flex align-items-center">
                    <div
                      class="flex-shrink-0"
                      style="width: 60px; height: 60px"
                    >
                      <img
                        :src="event.images.images[0].original_url"
                        :alt="event.title"
                        class="img-fluid rounded"
                        style="width: 100%; height: 100%; object-fit: cover"
                      />
                    </div>
                    <div class="ms-3 flex-grow-1">
                      <div class="d-flex justify-content-between">
                        <div class="fw-medium">{{ event.name }}</div>
                        <span class="small text-muted">{{
                          event.datetime_summary
                        }}</span>
                      </div>
                    </div>
                  </div>
                </RouterLink>
              </div>
            </div>
            <div v-else class="p-4 text-muted text-center">No events found</div>
          </template>
        </div>
      </div>
    </div>
  </div>
</template>

<script setup>
import { fetchEvents } from "@/api/eventsApi";
import { ref, watch, onMounted } from "vue";
import { useDebounceFn } from "@vueuse/core";
import anime from 'animejs/lib/anime.es.js';

// State
const backgroundImage = ref("/images/concert-hero.jpg");
const searchQuery = ref("");
const searchResults = ref([]);
const isSearching = ref(false);
const events = ref([]);

const performSearch = async (query) => {
  if (!query.trim()) {
    events.value = [];
    return;
  }

  try {
    isSearching.value = true;
    const response = await fetchEvents({
      order: "popularity",
      rows: 20,
    });
    let eventLs = [];
    for (let i of response) {
      if (i["name"].toLowerCase().includes(query)) {
        eventLs.push(i);
      }
    }
    events.value = eventLs;
    searchResults.value = events.value;
  } catch (error) {
    console.error("Failed to fetch events:", error);
  } finally {
    isSearching.value = false;
  }
};

// Debounced search
const debouncedSearch = useDebounceFn(performSearch, 300);

const handleSearch = () => {
  debouncedSearch(searchQuery.value);
};

const selectEvent = (event) => {
  console.log("Selected event:", event);
  // Add your event selection logic here
  // For example, emit an event or navigate to event details
};

const formatDate = (dateString) => {
  return new Date(dateString).toLocaleDateString();
};

// Animation on component mount
onMounted(() => {
  anime({
    targets: '.hero-title',
    opacity: [0, 1],
    translateY: [-20, 0],
    duration: 1000,
    easing: 'easeOutQuad',
  });

  anime({
    targets: '.search-box',
    opacity: [0, 1],
    translateY: [-20, 0],
    delay: 1000, // Add a delay for a sequential effect
    duration: 1500,
    easing: 'easeOutQuad',
  });
  anime({
  targets: '#ticketree path',
  strokeDashoffset: [anime.setDashoffset, 0],
  easing: 'easeInSine',
  duration: 1500,
  delay: function(el, i) { return i * 250 },
});
});
</script>

